spawn_players={}


function init_map()
  -- TEMP get spawns
  local i,j = 0, 0
  for i = 0,  (MAP_H-1) do
    for j = 0,  (MAP_W-1) do
      if map_data[i][j] == 8 then
        add(spawn_players, {x = j*8+4, y = i*8+4})
      end
    end
  end
  gen_mapsurf()
end

function draw_map()
  local scrnw, scrnh = screen_size()
  local x,y = cam.x, cam.y
  
  draw_surface(mapsurf, 0, 0, x, y, scrnw, scrnh)
end

mapsurf = nil
function gen_mapsurf()
  if server_only then return end
  
  local tile_map = {}
  for y = 0,MAP_H-1 do
    local line = {}
    local d_line = map_data[y]
    for x = 0,MAP_W-1 do
      local v = d_line[x]
      local n = 16
      if v == 0 then
        if chance(3) then
          n = 23
        elseif chance(15) then
          n = 16+irnd(6)
        end
      elseif v == 1 then
        local left = (d_line[x-1]  and d_line[x-1] >= 1 and d_line[x-1] < 5)
        local right = (d_line[x+1] and d_line[x+1] >= 1 and d_line[x+1] < 5)
        
        if left and right then
          n = 23+irnd(2)
        elseif left then
          n = 27
        elseif right then
          n = 26
        else
          n = 28
        end
        
      elseif v == 2 then
        local k = 0
        if d_line[x-1]==v then k = k+1 end
        if d_line[x+1]==v then k = k+2 end
        if y<=0 or map_data[y-1][x]==v then       k = k+4 end
        if y>=MAP_H-1 or map_data[y+1][x]==v then k = k+8 end
        n = 32+k
      elseif v == 3 then
        n = 31
      elseif v == 4 then
        n = 30
      end
      
      line[x] = n
    end
    tile_map[y] = line
  end
  
  mapsurf = new_surface(MAP_W*8, MAP_H*8)
  draw_to(mapsurf)
  palt(0,false)
  for y = 0,MAP_H-1 do
    local line = tile_map[y]
    for x = 0,MAP_W-1 do
      spr(line[x], x*8+4, y*8+4)
    end
  end
  
  draw_to()
  palt(0,true)
end
